import { Component, OnInit } from '@angular/core';
import { ClientModel } from 'src/app/models/client-model';
import { TodoModel } from 'src/app/models/todo-model';
import { ClientManagService } from 'src/app/services/client-manag.service';

@Component({
  selector: 'app-add-to-do',
  templateUrl: './add-to-do.component.html',
  styleUrls: ['./add-to-do.component.css']
})
export class AddToDoComponent implements OnInit{

    public clients: ClientModel[]

    public newTodo = new TodoModel()

    public constructor(private service: ClientManagService) {}

    async ngOnInit(): Promise<void> {
        try {
            this.clients = await this.service.getAllClient()    
        }
        catch (err: any) {
            console.log(err)            
        }        
    }


}
<div>

    <form>

        <label>Client:</label>
        <select [(ngModel)]="newTodo">
            <option selected value="">Select Client</option>
            <option *ngFor="let c of clients" value="c._id">{{c.name}} / {{c.business}}</option>
        </select>

        <label>Description:</label>
        <textarea></textarea>

        <button>ADD</button>
    </form>
</div>
